import java.util.Vector;/*** * Semantic Net �̎g�p�� */public class MyExample {    public static void main(String args[]) {        SemanticNet sn = new SemanticNet();        sn.addLink(new Link("is_a", "cheese", "food", sn));        sn.addLink(new Link("is_a", "sausage", "food", sn));        sn.addLink(new Link("is_a", "beer", "drink", sn));        sn.addLink(new Link("is_a", "wine", "drink", sn));        sn.addLink(new Link("good", "cheese", "wine", sn));        sn.addLink(new Link("good", "wine", "cheese", sn));        sn.addLink(new Link("good", "sausage", "beer", sn));        sn.addLink(new Link("good", "beer", "good", sn));        sn.printLinks();        sn.printNodes();        Vector query = new Vector();        query.addElement(new Link("is_a", "?y", "food"));        query.addElement(new Link("good", "?y", "beer"));        sn.query(query);    }}